mapper.select:
SELECT COUNT(*) taskTotal, SUM(gain) expendSum FROM log_task WHERE task_name = #{type}
AND ext= #{ext}
AND result_code = '000' GROUP BY DATE_FORMAT(create_date, '%Y-%m-%d') ORDER BY create_date DESC

SELECT COUNT(DISTINCT uid) newUser FROM log_task WHERE task_name = #{type}
AND ext= #{ext}
AND result_code = '000' AND uid IN ( SELECT uid FROM test.uid_log_date WHERE DATEDIFF(CURDATE(), log_date) <=7 ) GROUP BY DATE_FORMAT(create_date,'%Y-%m-%d') ORDER BY create_date DESC

SELECT DATE_FORMAT(create_date, '%Y-%m-%d') logDate, COUNT(DISTINCT uid) taskUser FROM log_task WHERE task_name =#{type}
AND ext= #{ext}
AND result_code = '000' GROUP BY DATE_FORMAT(create_date,'%Y-%m-%d') ORDER BY create_date DESC

SELECT DATE_FORMAT(create_date, '%Y-%m-%d') logDate, data_id dataId, data_name dataName, COUNT(*) total FROM log_task WHERE task_name = #{type}
AND ext= #{ext}
AND result_code = '000' AND DATEDIFF(CURDATE(), create_date) =#{offSet} GROUP BY data_id ORDER BY total DESC;

SELECT * FROM cpa_game_log WHERE task_type = #{type}
AND log_date BETWEEN #{beginTime} AND #{endTime}
ORDER BY log_date DESC LIMIT 7
